class Play

case object Rock extends Play

case object Paper extends Play

case object Scissors extends Play

val advMap: Map[String, Play] = Map("A" -> Rock, "B" -> Paper, "C" -> Scissors)
val ownMap: Map[String, Play] = Map("X" -> Rock, "Y" -> Paper, "Z" -> Scissors)

val scoreMap: Map[Play, Int] = Map(Rock -> 1, Paper -> 2, Scissors -> 3)

def process(input: String): Array[(String, String)] =
  input.split("\n").map(g => g.split(" ")).map(l => (l(0), l(1)))
def getScore(advPlay: Play, ownPlay: Play): Int =
  val winScore = (scoreMap(advPlay), scoreMap(ownPlay)) match
    case (1, 3) => 0
    case (3, 1) => 6
    case (a, b) if a > b => 0
    case (a, b) if a == b => 3
    case (a, b) if a < b => 6
  winScore + scoreMap(ownPlay)

def getTotalScore1(plays: Array[(String, String)]): Int =
  plays.map((l, r) => getScore(advMap(l), ownMap(r))).sum

def findPlay(advPlay: Play, outcome: String): Play =
  outcome match
    case "X" => advPlay match
      case Rock => Scissors
      case Paper => Rock
      case Scissors => Paper
    case "Y" => advPlay
    case "Z" => advPlay match
      case Rock => Paper
      case Paper => Scissors
      case Scissors => Rock

def getTotalScore2(plays: Array[(String, String)]): Int =
  plays.map((l, r) => getScore(advMap(l), findPlay(advMap(l), r))).sum
