def oneContainsOther(l1: Int, r1: Int, l2: Int, r2: Int): Boolean =
  def leftContainsRight(l1: Int, r1: Int, l2: Int, r2: Int): Boolean=
    l1 <= l2 & r2 <= r1
  leftContainsRight(l1, r1, l2, r2) | leftContainsRight(l2, r2, l1, r1)

def intersect(l1: Int, r1: Int, l2: Int, r2: Int): Boolean =
  (l1 <= l2 & l2 <= r1) | (l2 <= l1 & l1 <= r2)
