def firstMarker(s: String, n: Int): Int =
  s.sliding(n, step = 1).map(_.toSet.size).indexOf(n) + n

def firstPacketMarker(s: String): Int =
  firstMarker(s, n = 4)
def firstMessageMarker(s: String): Int =
  firstMarker(s, n = 14)
@main
def day6(): Unit =
  val input: String = ""
  val example: String = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
  println(s"Part 1: ${firstPacketMarker(input)}")
  println(s"Part 2: ${firstMessageMarker(input)}")